mapper.insert:

	SELECT FACILITY_SEQ.NEXTVAL
	  FROM DUAL

INSERT INTO FACILITY(
	FACILITY_NO
, DEPT_NO
, FACILITY_NAME
, MANAGEMENT
, FACILITY_DATE
, REMARKS
) VALUES (
	#{facilityNo}
, #{department.deptNo}
, #{facilityName}
, 0
, CURRENT_DATE
, #{remarks}
)

	INSERT INTO FACILITY_ATTACH (
		ATTACH_NO
	, FACILITY_NO
	, UPLOAD_PATH
	, FILESYSTEM_NAME
	, ORIGINAL_FILENAME
) VALUES (
		FACILITY_ATTACH_SEQ.NEXTVAL
	, #{facilityNo}
	, #{uploadPath}
	, #{filesystemName}
	, #{originalFilename}
	)
