mapper.select:
SELECT COUNT(*)
  FROM MESSAGE M JOIN EMPLOYEE ES ON M.MSG_SENDER = ES.EMP_NO JOIN EMPLOYEE ER ON M.MSG_RECEIVER = ER.EMP_NO
 WHERE M.MSG_RECEIVER = ${empNo}

SELECT COUNT(*)
  FROM MESSAGE M JOIN EMPLOYEE ES ON M.MSG_SENDER = ES.EMP_NO JOIN EMPLOYEE ER ON M.MSG_RECEIVER = ER.EMP_NO
 WHERE M.MSG_SENDER = #{empNo}

SELECT POS_NAME
  FROM POSITION
 WHERE POS_NO = #{posNo}

mapper.update:
UPDATE MESSAGE
   SET READ_YN = 'Y'
 WHERE MSG_NO = #{msgNo}

UPDATE MESSAGE
   SET STAR_YN = 'Y'
 WHERE MSG_NO = #{msgNo}
