mapper.insert:

  SELECT NOTICE_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO NOTICE_BOARD(
  NOTICE_NO
, BOARD_TITLE
, BOARD_CONTENTS
, BOARD_CREATE_DT
, BOARD_MODIFY_DT
, SIGNAL
, EMP_NO
) VALUES (
  #{noticeNo}
, #{boardTitle}
, #{boardContents}
, CURRENT_TIMESTAMP
, CURRENT_TIMESTAMP
, #{signal}
, #{employee.empNo}
)

INSERT INTO NOTICE_ATTACH (
  ATTACH_NO
, UPLOAD_PATH
, FILESYSTEM_NAME
, ORIGINAL_FILENAME
, NOTICE_NO
) VALUES (
  NOTICE_ATTACH_SEQ.NEXTVAL
, #{uploadPath}
, #{filesystemName}
, #{originalFilename}
, #{noticeNo}
)

mapper.select:
SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME 
  FROM NOTICE_ATTACH
 WHERE NOTICE_NO = #{noticeNo}

SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME
  FROM NOTICE_ATTACH
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE NOTICE_BOARD
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENTS = #{boardContents}
     , BOARD_MODIFY_DT = CURRENT_TIMESTAMP
     , SIGNAL = ${signal}
 WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE_BOARD
   SET HIT = HIT + 1
 WHERE NOTICE_NO = #{noticeNo}
