mapper.update:
UPDATE WORK
SET WORK_STATE = #{dayoffType}
WHERE WORK_DATE = TO_DATE(#{today}, 'YYYY-MM-DD')
  AND EMP_NO = #{empNo}

mapper.select:
SELECT COUNT(*) 
FROM WORK 
WHERE EMP_NO = #{empNo} 
  AND LATE_YN = 'Y'
  AND TO_CHAR(WORK_DATE, 'YYYY') = #{year}

SELECT COUNT(*) 
FROM WORK 
WHERE EMP_NO = #{empNo} 
  AND WORK_STATE = 40
  AND TO_CHAR(WORK_DATE, 'YYYY') = #{year}

SELECT COUNT(*) 
FROM WORK 
WHERE EMP_NO = #{empNo}
  AND WORK_STATE IN (10, 20) 
  AND TO_CHAR(WORK_DATE, 'YYYY') = #{year}

SELECT COALESCE(SUM(WORK_TOTAL_TIME), 0) 
FROM WORK 
WHERE EMP_NO = #{empNo}
  AND WORK_STATE IN ('10', '20') 
  AND TO_CHAR(WORK_DATE, 'YYYY') = #{year}

SELECT COALESCE(AVG(WORK_TOTAL_TIME), 0.0) 
FROM WORK 
WHERE EMP_NO = #{empNo}
  AND WORK_STATE IN (10, 20) 
  AND TO_CHAR(WORK_DATE, 'YYYY') = #{year}
